import Awarddata from './Awarddata';
import AwardCard from './AwardCard';



import pic1 from '../images/arrowleft.png';
import pic2 from '../images/arrowright.png';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import React, { Component } from "react";
import Slider from "react-slick";

export default class Awards extends Component {
  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  next() {
    this.slider.slickNext();
  }
  previous() {
    this.slider.slickPrev();
  }
  render() {
    const settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 2,
      slidesToScroll: 1
    };
    return (
      <div className='container mt-5 '>
        <h2 className='heading'>Previous and Next methods</h2>
        <Slider ref={c => (this.slider = c)} {...settings}>
           {
Awarddata.map((val,ind)=>{
  return  <div>

    <AwardCard date={val.date} imgsrc={val.imgsrc} para={val.para}/>

    </div>
})


           }
        </Slider>
        <div style={{ textAlign: "center" }}>

            <img src={pic1} alt="" srcset=""  className="button m-2 " onClick={this.previous} width="20px"   />
            <img src={pic2} alt="" srcset=""  className="button" onClick={this.next}   width="20px"   />

        </div>
      </div>
    );
  }
}
