import React, { useState } from "react";

import { Form } from "react-bootstrap";
// import pic from ''
const Contact = () => {
    const [d, Sdata] = useState({
        fullname: "",
        phone: "",
        email: "",
        msg:"",
    });


    const Inputfun = (event) => {
        const [name, value] = event.target;

        Sdata((preval) => {
            return {
                ...preval,
                [name]: value,
            };

        });

    };

    const Formsub = (e) => {
        e.preventDefault();
        alert("form submited successfully");
    };

    return (<>

        <div className="container mx-auto m-5">
            <div className="row">

                <div className="col-12 contact">


                    <div className="container contact">
                    <h1 className=" text-center heading my-5">Contact US</h1>
                        <div className="row d-flex justify-content-between">
<div className="col-md-5">

<img  src="https://cdn.pixabay.com/photo/2019/06/29/04/36/counselor-4305394__340.jpg" className="img-fluid contact-img Transition" alt="" />
</div>
                            <div className="col-md-5 ">

                                <Form onSubmit={Formsub}>

                                    <div className="mb-3">
                                        <label for="exampleFormControlInput1" className="form-label">FullName</label>
                                        <input type="text" value={d.fullname} name="fullname" onChange={Inputfun} className="form-control" id="exampleFormControlInput1" placeholder="Enter Your Name" />
                                    </div>

                                    <div className="mb-3">
                                        <label for="exampleFormControlInput1" className="form-label">Email</label>
                                        <input type="text" onChange={Inputfun} name="email" value={d.email} className="form-control" id="exampleFormControlInput1" placeholder="Eneter Your Email" />
                                    </div>


                                    <div className="mb-3">
                                        <label for="exampleFormControlTextarea1" className="form-label">Message</label>
                                        <textarea name="msg" onChange={Inputfun} value={d.msg} className="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                                    </div>


                                    <button type="submit" onChange={Inputfun} class="btn text-light">Send msg</button>
                                </Form>
                            </div>

                        </div>

                    </div>
                </div>
            </div>
        </div>
    </>);
}
export default Contact;
